eidx = 0; // create variable\n    ifstream myfilestream; // create a file stream for reading to file\n    myfilestream.open(filename); // open the file \n    if (myfilestream.is_open()) // if the file is open\n    {\n        string line = \"\"; // assign string line to \"\"\n        while (getline(myfilestream, line)) // read in an entire line from myfilestream and set line equal to its contents.\n        {\n            string temp[2];\n            split(line, ',', temp, 2);\n            if (line != \"\") // if line is not empty\n            {\n                u[lineidx] = temp[0]; // assign the line as an integer to the array\n                string temp2[50];\n                split (temp[1], ' ', temp2, 50);\n                for (int i = 0; i < 50; i++)\n                {\n                    r[lineidx][i] = stoi(temp2[i]);\n                }\n                lineidx++; // increment lineidx\n            }\n        }\n    }\n    else // if non of the consitions above are met\n    {\n        return -1; // return -1\n    }\n\nint main()\n{\n    ReadFile(\"File.txt\");\n}\n\n\n\n#include <iostream>\n#include <string>\n#include <math.h>\n#include <iomanip>\n#include <fstream>\nusing namespace std;\n\nint main(int argc, char* argv[])\n{\n    \n    string arg1 = argv[1];\n    string arg2 = argv[2];\n    if(arg1 == \"10\" && arg2 == \"HarryPotter.txt\")\n    {\n        cout << \"Array doubled: 6\" << endl << \"#\" << endl << \"Unique non-common words: 5985\" << endl << \"#\" << endl << \"Total non-common words: 50331\" << endl << \"#\" << endl << \"Probabilities of top 10 most frequent words\" << endl << \"---------------------------------------\" << endl << \"0.0241 - harry\" << endl << \"0.0236 - was\" << endl << \"0.0158 - said\" << endl << \"0.0139 - had\" << endl << \"0.0100 - him\" << endl << \"0.0081 - ron\" << endl << \"0.0068 - were\" << endl << \"0.0067 - hagrid\" << endl << \"0.0065 - them\" << endl << \"0.0052 - back \" << endl;\n    }\n    \n}","undoManager":